void ProcessScreenViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &BtnStartFuming)
    {
        //StartFuming
        //When BtnStartFuming clicked call virtual function
        //Call NextStage
        NextStage();
    }
    else if (&src == &BtnStopFuming)
    {
        //ShowEndFumingConfirmation
        //When BtnStopFuming clicked call virtual function
        //Call ConfirmStopFuming
        ConfirmStopFuming();
    }
    else if (&src == &BtnHome)
    {
        //GotoHome
        //When BtnHome clicked change screen to MainScreen
        //Go to MainScreen with no screen transition
        application().gotoMainScreenScreenNoTransition();
    }
    else if (&src == &BtnBack)
    {
        //GotoMainScreen
        //When BtnBack clicked change screen to MainScreen
        //Go to MainScreen with slide screen transition towards South
        application().gotoMainScreenScreenSlideTransitionSouth();
    }
    else if (&src == &BtnAbort)
    {
        //ShowAbortWindow
        //When BtnAbort clicked call virtual function
        //Call ConfirmAbort
        ConfirmAbort();
    }
    else if (&src == &BtnStartProcess)
    {
        //StartProcess
        //When BtnStartProcess clicked call virtual function
        //Call NextStage
        NextStage();
    }
    else if (&src == &BtnAbortNo)
    {
        //HideAbortWindow
        //When BtnAbortNo clicked hide AbortWindow
        //Hide AbortWindow
        AbortWindow.setVisible(false);
        AbortWindow.invalidate();
    }
    else if (&src == &BtnAbortYes)
    {
        //AbortPumpDown
        //When BtnAbortYes clicked call virtual function
        //Call AbortProcess
        AbortProcess();
    }
    else if (&src == &BtnStopFumingNo)
    {
        //HideEndFumingConfirmation
        //When BtnStopFumingNo clicked hide StopFumingWindow
        //Hide StopFumingWindow
        StopFumingWindow.setVisible(false);
        StopFumingWindow.invalidate();
    }
    else if (&src == &BtnStopFumingYes)
    {
        //AbortFuming
        //When BtnStopFumingYes clicked call virtual function
        //Call AbortProcess
        AbortProcess();
    }
}
